mapper.select:
select * from tab_iopm_first_clues_classify

	and first_classify=#{first_classify}
	and zero_id=#{zero_id}

select * from(select l.* ,rownum rn from(select * from(select * from(select a.id,a.name,to_char(a.keyword2) keyword2,to_char(a.keyword3) keyword3,a.first_id,a.second_id,a.second_classify,a.update_time,a.record_time,a.first_classify, a.zero_id,a.zero_name from(select * from tab_iopm_clues t join tab_iopm_second_clues_classify p on (t.second_id = p.second_classify_id) join tab_iopm_first_clues_classify i on (t.first_id = i.first_classify_id) join tab_iopm_zero_clues_classify z on (t.zero_id = z.zero_id) where t.second_id is not null and z.zero_id=#{zero_id}
and ((name like concat(concat('%',#{name}),'%')) or (keyword3 like concat(concat('%',#{name}),'%')) or (keyword2 like concat(concat('%',#{name}),'%')))
and i.first_classify=#{classify_name} and second_classify=#{classifyTwo_name} ) a where 1=1 ) temp
union select * from(select a.id,a.name,to_char(a.keyword2) keyword2,to_char(a.keyword3) keyword3,a.first_id,a.second_id,'',a.update_time,a.record_time,a.first_classify, a.zero_id,a.zero_name from(select * from tab_iopm_clues t join tab_iopm_first_clues_classify i on (t.first_id = i.first_classify_id) join tab_iopm_zero_clues_classify z on (t.zero_id = z.zero_id) where t.second_id is null and z.zero_id=#{zero_id}
and ((name like concat(concat('%',#{name}),'%')) or (keyword3 like concat(concat('%',#{name}),'%')) or (keyword2 like concat(concat('%',#{name}),'%')))
and i.first_classify=#{classify_name} ) a where 1=1 ) temp) b order by update_time desc) l where 1=1
and
ROWNUM <=(#{start} * #{limit})
) g where 1=1
and RN >= (#{start} - 1)* #{limit} +1

select * from tab_iopm_second_clues_classify where
first_classify_id=#{first_classify_id} and
second_classify=#{second_classify}

		select * from tab_iopm_clues where 1=1
		
			and id!=#{id}
       
		
			and name=#{name}

		select count(*) from tab_iopm_second_clues_classify where 1=1
		
			and second_classify=#{classifyTwo_name}
      
		
			and second_classify_id!=#{classifyTwo_id}

mapper.update:
update tab_iopm_clues
first_id=#{classify_id}, zero_id=#{zero_id},
where first_id=#{classifyTwo_id}

update TAB_IOPM_CLUES
second_id=#{classify_id}, first_id=#{classify2}, zero_id=#{zero_id},
where second_id=#{classifyTwo_id}

update tab_iopm_second_clues_classify
second_classify_id=#{classify_id}, first_classify_id=#{classify2},
where second_classify_id=#{classifyTwo_id}

mapper.select:
select * from tab_iopm_first_clues_classify where 1=1
and
FIRST_CLASSIFY_ID <>#{no}

select * from tab_iopm_second_clues_classify where 1=1
and
SECOND_CLASSIFY_ID <>#{no}

select * from tab_iopm_clues

   and keyword2 like concat(concat('%',#{keyword2}),'%')
   and id!=#{id}
