mapper.select:
select * from TAB_IOPM_CLUES

	and zero_id=#{zero_id}
	and first_id=#{first_classify_id}
	and second_id=#{second_classify_id}

select count,rn,positive,negative,neutral,name,id from(select d.*, rownum rn from(select t.* from(select count(keywords) count,c.name, count(case when emotion=1 then 1 else null end)positive,count(case when emotion=2 then 2 else null end)negative, count(case when emotion=3 then 3 else null end)neutral,c.id from TAB_IOPM_ARTICLE_INFO a,TAB_IOPM_CLUES c,TAB_IOPM_ARTICLE_CLUES_SRC b
and b.article_id=a.id and b.clues_id=c.id and c.zero_id=#{zero_id} and c.first_id=#{first_classify_id} and c.second_id=#{second_classify_id}
group by c.id,c.name)t order by t.count desc)d ) g

select count,rn,positive,negative,neutral,name,id from(select d.*, rownum rn from(select t.* from(select count(keywords) count,c.name, count(case when emotion=1 then 1 else null end)positive,count(case when emotion=2 then 2 else null end)negative, count(case when emotion=3 then 3 else null end)neutral,c.id from TAB_IOPM_ARTICLE_INFO a,TAB_IOPM_CLUES c,TAB_IOPM_ARTICLE_CLUES_SRC b
and b.article_id=a.id and b.clues_id=c.id and c.zero_id=#{zero_id} and c.first_id=#{first_classify_id} and c.second_id=#{second_classify_id}
group by c.id,c.name)t order by negative desc)d ) g

select * from(select rownum rn, f.* from( select t.keywords,t.title,t.content,t.url,t.website_name,t.image_url,t.release_time, t.emotion,t.review_count,t.comment_count,t.author,t.info_type,t.up_count,t.transmit_count,t.id, (select count(x.id) from tab_iopm_user_action x where x.msg_id = t.id and x.action_type=301) adoptCount, (select count(x.id) from tab_iopm_user_action x where x.msg_id = t.id and x.action_type=302) notAdoptCount from TAB_IOPM_CLUES c,TAB_IOPM_ARTICLE_INFO t,TAB_IOPM_ARTICLE_CLUES_SRC s TAB_IOPM_ARTICLE_INFO t
and t.release_time >= to_date(#{release_time},'yyyy-mm-dd hh24:mi:ss')
and t.record_time <= to_date(#{record_time},'yyyy-mm-dd hh24:mi:ss')
and t.info_type in
#{item}
and t.info_type = 9
and c.id=#{clus_ids}
and t.hot_id=#{hot_id}
and (t.emotion is null
or t.emotion in
#{item}
or t.emotion = 0 )
and t.emotion in
#{item}
and t.emotion = 0
and t.is_vip = #{spread}
and(
t.title like concat(concat('%',#{item}),'%')
)
and t.clues_ids is not null and s.article_id=t.id and s.clues_id=c.id and c.zero_id=#{zero_id} and c.first_id=#{first_classify_id} and c.second_id=#{second_classify_id}
order by t.weight desc,t.release_time desc order by t.release_time desc order by t.comment_count desc )f where 1=1
and
ROWNUM <=(#{pageNo} * #{pageSize})
) g where 1=1
and RN >= (#{pageNo} - 1)* #{pageSize} +1

	    select count(t.id)
		from 
		TAB_IOPM_CLUES c,TAB_IOPM_ARTICLE_INFO t,TAB_IOPM_ARTICLE_CLUES_SRC s
		TAB_IOPM_ARTICLE_INFO t
		
			
				and 
