select count(t.id) count,emotion count(info_type) count,info_type count(zero_id) count,zero_id count(first_id) count,first_id count(second_id) count,second_id from TAB_IOPM_CLUES c,TAB_IOPM_ARTICLE_INFO t,TAB_IOPM_ARTICLE_CLUES_SRC s TAB_IOPM_ARTICLE_INFO t
and t.release_time >= to_date(#{release_time},'yyyy-mm-dd hh24:mi:ss')
and t.record_time <= to_date(#{record_time},'yyyy-mm-dd hh24:mi:ss')
and t.info_type in
#{item}
and t.info_type = 9
and c.id=#{clus_ids}
and t.hot_id=#{hot_id}
and (t.emotion is null
or t.emotion in
#{item}
or t.emotion = 0 )
and t.emotion in
#{item}
and t.emotion = 0
and t.is_vip = #{spread}
and
t.title like concat(concat('%',#{item}),'%')
and t.clues_ids is not null and s.article_id=t.id and s.clues_id=c.id and c.zero_id=#{zero_id} and c.first_id=#{first_classify_id} and c.second_id=#{second_classify_id}
group by emotion group by info_type group by zero_id group by first_id group by second_id

select count(t.id) count from TAB_IOPM_USER_ACTION t

	
		and t.msg_id = #{msg_id}
	
	
		and t.user_id = #{user_id}
	
	
